class GoldSequence
(
  n: Int = 4,
) extends LazyModule()(Parameters.empty) {
  val streamNode = AXI4StreamMasterNode(AXI4StreamMasterPortParameters(AXI4StreamMasterParameters(
      name = name,
      n = n,
      u = 0,
    )))

  lazy val module = new LazyModuleImp(this) with HasGoldTable {
    val streamOut = streamNode.out.head._1
    // val cnt = RegInit(0.U(12.W))

    // val registers = table.map(t => RegInit(t))
    val state = RegInit(tableUInt)
    val stateMSB = state(table.length - 1)

    when (streamOut.fire()) {
      state := (state << 1) | stateMSB
      // circularly shift everything
      // for ((l, r) <- registers.zip(registers.tail)) {
      //   l := r
      // }
      // registers.last := registers.head
      // cnt := Mux(cnt === 4094.U, 0.U, cnt +% 1.U)
    }
    val minusOneWhateverSize = -32767.S.asUInt
    val minusOne = Wire(UInt(16.W))
    minusOne := minusOneWhateverSize
    val one = 32767.U(16.W)
    val zeroVal = util.Cat(minusOne, minusOne)
    val oneVal = util.Cat(one, one)
    val tableValue = Mux(stateMSB === 0.U, zeroVal, oneVal)
    streamOut.bits.data := tableValue
    streamOut.bits.dest := 0.U
    streamOut.bits.id   := 0.U
    streamOut.bits.keep := 0.U
    streamOut.bits.last := false.B // cnt === 4094.U
    streamOut.bits.user := 0.U
    streamOut.bits.strb := BigInt("1" * streamNode.out.head._2.bundle.n, 2).U
    streamOut.valid := true.B
  }
}

object GoldSequence {
  def apply(n: Int = 4)(implicit valName: ValName): AXI4StreamMasterNode = LazyModule(new GoldSequence(n = n)).streamNode
}
